class Cleaner(object):

    def __init__(self):
        self.str = ''

    @staticmethod
    def clean_text(list):

        if '#REDIRECT' in list[0]:
            list[0] = list[0].replace('#REDIRECT', 'is similar to')
            list[0] = list[0].replace('[', '')
            list[0] = list[0].replace(']', '')
            string = list[0]

        else:

            """level: list"""
            # find the first string with '''titlename'''
            for i in range(len(list)):
                if '\'\'\'' in str(list[i]):
                    break
                elif len(list) != 0:
                    list[i] = ''

            # delete all elements before '''titlename'''
            for text in list:
                if '' in list:
                    list.remove('')

            # find the first element with '=='
            index = 0
            for i in range(len(list)):
                if '==' in list[i]:
                    index = i
                    break

            # delete all elements after the element with '==', and this element is also deleted
            if index < len(list):
                for i in range(len(list)):
                    if i >= index:
                        list.pop()

            index_start = 0
            index_end = 0

            # delete all <ref> content </ref>
            while '/ref' in list:
                for i in range(len(list)):
                    if list[i] == '<' and 'ref' in list[i + 1]:
                        index_start = i
                        break
                for i in range(len(list)):
                    if list[i] == '/ref' and list[i + 1] == '>':
                        index_end = i + 1
                        break
                for i in range(index_start, index_end + 1):
                    list[i] = ''
                for text in list:
                    if '' in list:
                        list.remove('')

            """level: list element"""
            # delete {{content}} in an element
            for i in range(len(list)):
                if '{{' in list[i]:
                    i_start = list[i].find('{{')
                    i_end = list[i].find('}}')
                    if i_start != -1 and i_end != -1:
                        a = list[i][0: i_start]
                        b = list[i][i_end + 2: len(list[i])]
                        list[i] = a + b

            # delete web-address
            for i in range(len(list)):
                if 'https://www' in list[i]:
                    i_start = list[i].find('https://www')
                    i_end = list[i][i_start:].index(' ')
                    i_end += i_start
                    if i_start != -1 and i_end != -1:
                        a = list[i][0: i_start]
                        b = list[i][i_end + 1: len(list[i])]
                        list[i] = a + b

            # delete all brackets of [[ content ]] and delete '''
            for i in range(len(list)):
                list[i] = list[i].replace('[', '')
                list[i] = list[i].replace(']', '')
                list[i] = list[i].replace('\'\'\'', '')

            string = ''.join(list)

        string = string.rstrip()
        return string

